import React from "react";

const SpeakerCard = ({ image, name, title, company, linkedin }) => (
  <a 
    href={linkedin} 
    target="_blank" 
    rel="noopener noreferrer" 
    className="relative p-4 bg-white shadow-lg rounded-lg flex flex-col items-center transition-transform transform hover:scale-105 hover:shadow-2xl hover:bg-gradient-to-br from-gray-100 to-blue-100 border hover:border-blue-400 hover:border-opacity-50"
  >
    <div className="absolute inset-0 border-b-4 border-transparent hover:border-blue-500 transition-all duration-300 rounded-lg"></div>
    <img src={image} alt={name} className="w-24 h-24 rounded-full mb-4 transition-transform duration-300 transform hover:rotate-12 hover:scale-110" />
    <h3 className="font-bold text-lg relative z-10 text-center">{name}</h3>
    <p className="text-sm text-gray-700 relative z-10 text-center">{title}</p>
    <p className="text-sm text-gray-500 relative z-10 text-center">{company}</p>
  </a>
);

const SpeakerSection = ({ title, speakers }) => (
  <div className="w-full md:w-full px-4">
    <h2 className="text-2xl font-extrabold text-center mb-8 text-blue-700">{title}</h2>
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
      {speakers.map((speaker, index) => (
        <SpeakerCard
          key={index}
          image={speaker.image}
          name={speaker.name}
          title={speaker.title}
          company={speaker.company}
          linkedin={speaker.linkedin} // LinkedIn profile link
        />
      ))}
    </div>
  </div>
);

